ChatBoxUI.Script:
const chatToggleBtn = document.getElementById("chatToggle");
const chatWrapper = document.getElementById("chatWrapper");
const chatCloseBtn = document.getElementById("chatCloseBtn");
const chatForm = document.getElementById("chatForm");
const chatInput = document.getElementById("chatInput");
const chatMessages = document.getElementById("chatMessages");

chatToggleBtn.addEventListener("click", () => {
  chatWrapper.style.display = "flex";
  chatToggleBtn.style.display = "none";
  chatInput.focus();
});

chatCloseBtn.addEventListener("click", () => {
  chatWrapper.style.display = "none";
  chatToggleBtn.style.display = "flex";
});

chatForm.addEventListener("submit", (e) => {
  e.preventDefault();
  const text = chatInput.value.trim();
  if (!text) return;
  addUserMessage(text);
  chatInput.value = "";
  setTimeout(() => {
    addAgentMessage(`MÃ¬nh Ä‘Ã£ nháº­n Ä‘Æ°á»£c: "${text}" ðŸ˜Š`);
  }, 400);
});

function addUserMessage(content) {
  const row = document.createElement("div");
  row.className = "message-row message-user";
  const bubble = document.createElement("div");
  bubble.className = "bubble-user";
  bubble.textContent = content;
  const time = document.createElement("div";
  time.className = "msg-time";
  time.textContent = getTimeLabel();
  row.appendChild(bubble);
  row.appendChild(time);
  chatMessages.appendChild(row);
  scrollToBottom();
}

function addAgentMessage(content) {
  const row = document.createElement("div");
  row.className = "message-row message-agent";
  const bubble = document.createElement("div");
  bubble.className = "bubble-agent";
  bubble.textContent = content;
  const time = document.createElement("div");
  time.className = "msg-time";
  time.textContent = getTimeLabel();
  row.appendChild(bubble);
  row.appendChild(time);
  chatMessages.appendChild(row);
  scrollToBottom();
}

function scrollToBottom() {
  chatMessages.scrollTop = chatMessages.scrollHeight;
}

function getTimeLabel() {
  const now = new Date();
  return now.getHours().toString().padStart(2, "0") + ":" + now.getMinutes().toString().padStart(2, "0");
}
